        "Search": function() {
        	//Variables
            var placeholder = "I'm looking for...";

            $("#gb-search-input").focus(function() {
                if($(this).val() == placeholder) {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val(placeholder);
                }
            });

            // FORM SUMBIT
            $("#gb-search-form").submit(function(e) {
            	e.preventDefault();
            	if($("#gb-search-input").val() != "" && $("#gb-search-input").val() != placeholder) {
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                }
            });
        },

        /* General Methods */
        "AllyClick": function(event) {
            if(event.type == "click") {
            	event.preventDefault();
                return true;
            } else if(event.type == "keypress" || event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
            	event.preventDefault();
                return true;
            } else {
                return false;
            }
        },

        "GatherMMGs": function() {
            if($(".hp .multimedia-gallery").length) {
            	var mmgArray = [];
                $(".hp .multimedia-gallery").each(function(){
                	mmgArray.push({ "appID": "#mmg-container-"+$(this).attr("data-pmi"), "loc": $(this).offset().top+$(this).outerHeight(), "scrollLock": 0 });
                });
                return mmgArray;
            }
        },

        "CutHex": function(hex){
        	return (hex.charAt(0)=="#") ? hex.substring(1,7):hex;
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

        "HexToRgb": function(hex){
        	var r = parseInt((this.CutHex(hex)).substring(0,2),16);
	        var g = parseInt((this.CutHex(hex)).substring(2,4),16);
	        var b = parseInt((this.CutHex(hex)).substring(4,6),16);
	        return r+', '+g+', '+b;
        },

        "LoadImage": function(element, image, defaultImage, toggle) {
        	//Variables
            image = $.trim(image);

            if(toggle) {
            	var srcCheck = image.indexOf("Faces/default-man.jpg") > -1;

                if((image != "") && (!srcCheck)) {
                    $(element).append('');
                } else {
                    $(element).append('');
                }
            }

        }
