        window.resizeMiniCalendar20200419032412310 = function () {
        var calId = 'miniCal20200419032412310';
        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200419032412310';
        var calMonthFullId = 'miniCalMonthFull20200419032412310';
        var calMonthShortId = 'miniCalMonthShort20200419032412310';
        var calGoToCalFullId = 'miniCalGotToCalFull20200419032412310';
        var calGoToCalShortId = 'miniCalGoToCalShort20200419032412310';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

