    function InsertRating159216202() {
    var ModuleInstanceID = 1592;
    var FlexDataID = 16202;
    var Rating = $('#swmoduleinstancerating-hid-rating159216202').val();
    var data = "{ModuleInstanceID: " + ModuleInstanceID + ", " +
                "FlexDataID: " + FlexDataID + ", " +
                "Rating: " + Rating + "} ";  
    var success = 'LoadRating159216202();';
    var failure = "CallControllerFailure(result[0].errormessage);";

    if (Rating != '' && Rating !== undefined) {
        CallController("https://www.frsd.k12.nj.us//site/UserControls/Rating/RatingController.aspx/InsertRating", data, success, failure);
    }
}

    $(document).ready(function() {
        CheckScript('Rating');
        CheckScript('Metadata');

        // initialize rating script
        $("input[name='swmoduleinstanceratinghidrating159216202']").rating({
            callback: function() {
                Getswmoduleinstanceratinghidrating159216202Values();
                InsertRating159216202($('#swmoduleinstancerating-hid-rating159216202').val());
            }
        });
    });
    
    // fill hidden input with value clicked
    function Getswmoduleinstanceratinghidrating159216202Values() {
        var selItem;

        $("input[name='swmoduleinstanceratinghidrating159216202']").each(function(i) {
            if ($(this).is(':checked')) {
                $('#swmoduleinstancerating-hid-rating159216202').val((i + 1) / 2);
            }
        });
    }

