    var success = 'LoadRating159216419();';
    var failure = "CallControllerFailure(result[0].errormessage);";

    if (Rating != '' && Rating !== undefined) {
        CallController("https://www.frsd.k12.nj.us//site/UserControls/Rating/RatingController.aspx/InsertRating", data, success, failure);
    }
}

