            callback: function() {
                Getswmoduleinstanceratinghidrating159216419Values();
                InsertRating159216419($('#swmoduleinstancerating-hid-rating159216419').val());
            }
        });
    });
    
    // fill hidden input with value clicked
    function Getswmoduleinstanceratinghidrating159216419Values() {
        var selItem;

        $("input[name='swmoduleinstanceratinghidrating159216419']").each(function(i) {
            if ($(this).is(':checked')) {
                $('#swmoduleinstancerating-hid-rating159216419').val((i + 1) / 2);
            }
        });
    }

