						var edlioJssorSlider = new $JssorSlider$('edlio_slider_container_8013', options);


						function resizeEdlioSlider () {
							var parentWidth = document.getElementById('edlio_slider_container_8013').parentElement.clientWidth;

							if (parentWidth) {
								edlioJssorSlider.$ScaleWidth(parentWidth);
							} else {
								window.setTimeout(resizeEdlioSlider, 30);
							}
						}


							resizeEdlioSlider();







							window.addEventListener("load", resizeEdlioSlider);
							window.addEventListener("resize", resizeEdlioSlider);
							window.addEventListener("orientationchange", resizeEdlioSlider);


					})($JssorSlider$);

function gt(value) {
	if(!value) {
		alert("Empty");
		return;
	}
	var windowResult = false;
	try{
		windowResult = window.open(value);
		if (!!!windowResult) window.location.href=value;
	} catch (e) {
		alert(e);
	}
}

body.script:
	var jq17 = jQuery.noConflict(true);
	jq17(function() {
		var pull 		= jq17('#mtoggle');
			menu 		= jq17('#topnav ul');
			menuHeight	= menu.height();

		jq17(pull).on('click', function(e) {
			e.preventDefault();
			menu.slideToggle();
		});

		jq17(window).resize(function(){
    		var w = jq17(window).width();
    		if(w > 550 && menu.is(':hidden')) {
    			menu.removeAttr('style');
    		}
		});
	});
