        window.resizeMiniCalendar20200419034518405 = function () {
        var calId = 'miniCal20200419034518405';
        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200419034518405';
        var calMonthFullId = 'miniCalMonthFull20200419034518405';
        var calMonthShortId = 'miniCalMonthShort20200419034518405';
        var calGoToCalFullId = 'miniCalGotToCalFull20200419034518405';
        var calGoToCalShortId = 'miniCalGoToCalShort20200419034518405';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
