        		$("html, body").animate({ scrollTop: $("body").offset().top}, "slow");
                $("li.sw-channel-item:first-child a").focus();
        });

        $("#back-to-top").keydown(function(e){
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
        		$("html, body").animate({ scrollTop: $("body").offset().top}, "slow");
                $("li.sw-channel-item:first-child a").focus();
