/* Menu Columns */
  $('.nav-lists.level1').each(function(){
    var $level1 = $(this);
    var catCount = $level1.children().length;

    if( catCount < 4 ){
      $level1.attr('data-column-count',catCount);

      $level1.children().each(function(){
        var $cat = $(this);
        var catItems = $cat.children('li').length;
        var catCols = 5 - catCount;
        var catWidth = 'calc('+ 25 * catCols +'% - 1em)';

        $cat.children('span').css('column-span','all');

        if( catItems > 8 ){
          $cat.css({
            'column-count' : catCols,
            'width' : catWidth
          });
        }
      });
    }
  });

});

	//move search bar out of pubwrapper in responsive
	$(document).ready(function(){
		$(window).resize(function() {
			var wi = $(window).width();
			var searchOut = $("#pubWrapper ul.pubList.fr #searchPanel").length;
			if (wi <= 800 && searchOut> 0) {
					$("#pubWrapper #searchPanel").appendTo('#pubWrapper');
				}
			
			if (wi >= 800 && searchOut == 0){
					$("#pubWrapper #searchPanel").prependTo('#pubWrapper ul.pubList.fr');
				}
		}); 
		$(window).resize(); //on page load 
	});

