div.script:
$(document).ready(function () {

    var $rotator = $(".rotator");
    $rotator.find("img:gt(0)").hide();
    setTimeout(Rotate, 5000);

    function Rotate() {
        var $current = $rotator.find("img:visible");
        var $next = $current.next();
        if ($next.length == 0) $next = $rotator.find("img:eq(0)");
        $current.fadeOut(2000);
        $next.fadeIn(2000);
        setTimeout(Rotate, 7000);
    }

});

$(document).ready(function () {

    var $rotator2 = $(".rotator2");
    $rotator2.find("img:gt(0)").hide();
    setTimeout(Rotate, 7000);

    function Rotate() {
        var $current = $rotator2.find("img:visible");
        var $next = $current.next();
        if ($next.length == 0) $next = $rotator2.find("img:eq(0)");
        $current.fadeOut(2000);
        $next.fadeIn(2000);
        setTimeout(Rotate, 7000);
    }

});

function googleTranslateElementInit() {
  new google.translate.TranslateElement({
	pageLanguage: 'en',
	
	layout: google.translate.TranslateElement.InlineLayout.SIMPLE
  }, 'google_translate_element');
}

