//adding the ability to record hits - 7/18/14 MPR - simply adds date and time of video played
 function videoHit(hpVideo)
		{$.ajax({url: "videos/playCount.cfm?hpVideo=" + hpVideo})}	

    function ajaxContent(requestUrl){
        $('#sw-ajaxData').html('');
        $('#sw-ajaxData').addClass('loading');
        if(requestUrl != undefined && requestUrl != null && requestUrl != '' && requestUrl != '#'){
    
            $.ajax({url: requestUrl,
                success: function(data) { 
                    $('#sw-ajaxData').removeClass('loading');
                    $('#sw-ajaxData').html(data);
                },
                error: function(){
                    $('#sw-ajaxData').html('CRITICAL FAILURE');
                }
            });//$.ajax
        }
    }//ajaxContent
    
    $(function() {
        $( "#sw-dpMiniCal" ).datepicker({
			onSelect: function(date){
				//alert('you picked: ' + date);
				ajaxContent('ajax/calendarAjax.cfm?mydate='+date);
        
			}
		});
	});
