    $(function() {
    	CreativeTemplate.Init();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "svg": { 
        	"more": ""
        },
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.JsMediaQueries();
            this.MyStart();
            this.Header();
            this.SocialMedia();
            this.Body();
            this.Footer();
            this.Dropdown();
            this.Slideshow();
            this.RsMenu();
            this.Schools();
            this.AppAccordion();
            this.Search();
            this.GlobalIcons();

            $(document).ready(function(){

                _this.MegaMenu();
                _this.ChannelBar();
                _this.Navigation(); 
                if($('#dashboard-sidebar').size()) {
                	$('nav').addClass('sidebar-active');
                }
                $('#sw-mystart-outer').remove();

            });
            $(window).load(function(){ 
                _this.WindowLoad();
            });
            $(window).resize(function(){ _this.WindowResize(); });

            // HANDLE LINKS THAT OPEN IN A NEW WINDOW
			csGlobalJs.OpenInNewWindowWarning();
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
        	$("#sw-search-input").unbind();
            $("#gb-search-form").on("keydown", "#sw-search-input", function(e) {
                if(e.which == 13) {
                    e.preventDefault();

                    if($.trim($("#sw-search-input").val()) != "I am looking for..." && $.trim($("#sw-search-input").val()) != "") {
                        SWGoToSearchResultsPageswsearchinput();
                    }
                }

            });
            this.CalendarListView();
            // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();

            $('.cs-mega-schools-column li a').click(function(){
                window.location.href = $(this).attr('href');
            }).keydown(function(e){

                var c = e.which;

                if(c == KeyCodes.enter){
                    window.location.href = $(this).attr('href');
                }
            });
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.CalendarListView();
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
                	//--> District and Signout Button right align
					$('.footer.column.two').addClass('cs-jfe').removeClass('cj-jfs');
                    //--> Move Site Map to Right side of footer
                    $('#gb-schoolwires-footer').append($('#gb-sitemap'));
                    //--> Allow Buttons to go full height
                	$('.gb-header.right').removeClass('cs-aic');
                break;
                case "768":
                	//--> Vertical center Menu Btn
                	$('.gb-header.right').addClass('cs-aic');
                    //--> District and Sign Out button left align
					$('.footer.column.two').removeClass('cs-jfe').addClass('cj-jfs');
                    //--> Move Site Map button above bb footer
                    $('#gb-schoolwires-footer').prepend($('#gb-sitemap'));
                break;
                case "640":
                	//--> Move header right to the right
                    $('#gb-header').append($('.gb-header.right'));
                	//--> Vertical center Menu Btn
                	$('.gb-header.right').addClass('cs-aic');
					$('.footer.column.two').removeClass('cs-jfe').addClass('cj-jfs');
                    //--> Put sitemap after bb footer
                    $('#gb-schoolwires-footer').append($('#gb-sitemap'));
                break;
                case "480":
                	//--> Move header right above header left
                    $('#gb-header').prepend($('.gb-header.right'));
                	//--> Center Buttons
					$('.footer.column.two').removeClass('cs-jfe').removeClass('cs-jfs').addClass('cs-jc');
                    //--> Put sitemap before bb footer
                    $('#gb-schoolwires-footer').prepend($('#gb-sitemap'));
                    //--> Left Align BB Footer Content
                    $('.bb-footer-left').removeClass('cs-jc').addClass('cs-jfs');
                    //--> Add header arrows
                    if(!$('.hp .ui-widget-header > svg').length) {
                    	$('#hp-content .ui-widget-header').append('');
                    }

                break;
                case "320":
                	//--> Move header right above header left
                    $('#gb-header').prepend($('.gb-header.right'));
                	//--> Center Buttons
					$('.footer.column.two').removeClass('cs-jfe').removeClass('cs-jfs').addClass('cs-jc');
                    //--> Put sitemap before bb footer
                    $('#gb-schoolwires-footer').prepend($('#gb-sitemap'));
                    //--> Center BB Footer Content
                    $('.bb-footer-left').addClass('cs-jc').removeClass('cs-jfs');
                    //--> Add header arrows
                    if(!$('.hp .ui-widget-header > svg').length) {
                    	$('#hp-content .ui-widget-header').append('');
                    }

                break;
            }
        },

        "Navigation" : function() {

            //--> Navigatoin Modifications
            $('.navigation .ui-widget-detail li').each(function(){
                if($(' > .bullet', this).hasClass('collapsible') || $(' > .bullet', this).hasClass('expandable')) {
                    $(' > .bullet', this).append('');
                }
            });

        },

        "Schools": function(){

            var _this = this,
                KeyCodes = _this.KeyCodes;

        	if (!$('#gb-mega-schoollist-wrapper').length) {

                $(function() {
                    var elemSchools = [],
                        elemHTML = '',
                        middleSchools = [],
                        middleHTML = '',
                        highSchools = [],
                        highHTML = '',
                        choiceSchools = [],
                        choiceHTML = '',
                        alternativeSchools = [],
                        alternativeHTML = '';

                    for(var i in schoolList) {
                        if(schoolList[i].schoolVisible.toLowerCase() == 'true') {

                            if(schoolList[i].schoolType.toLowerCase() == "elementary") {
                                elemSchools.push({"url": schoolList[i].schoolUrl, "name": schoolList[i].schoolName });
                            }
                            if(schoolList[i].schoolType.toLowerCase() == "middle") {
                                middleSchools.push({"url": schoolList[i].schoolUrl, "name": schoolList[i].schoolName });
                            }
                            if(schoolList[i].schoolType.toLowerCase() == "high") {
                                highSchools.push({"url": schoolList[i].schoolUrl, "name": schoolList[i].schoolName });
                            }
                            if(schoolList[i].schoolType.toLowerCase() == "choice") {
                                choiceSchools.push({"url": schoolList[i].schoolUrl, "name": schoolList[i].schoolName });
                            }
                            if(schoolList[i].schoolType.toLowerCase() == "alternative" || schoolList[i].schoolType.toLowerCase() == "other" || schoolList[i].schoolType.toLowerCase() == "specialty") {
                                alternativeSchools.push({"url": schoolList[i].schoolUrl, "name": schoolList[i].schoolName });
                            }

                        }
                    }

                    var n = 0,
                        cOne = '',
                        c1 = 1,
                        cTwo = '',
                        c2 = 1,
                        cThree = '',
                        c3 = 1,
                        cFour = '',
                        c4 = 1;

                    for(var s in elemSchools) {


                        if (n == 0) {

                            cOne += '
' + elemSchools[s].name + '';
                            n++;
                            c1++;

                        } else if (n == 1) {

                            cTwo += '
' + elemSchools[s].name + '';
                            n++;
                            c2++;

                        } else if (n == 2) {

                            cThree += '
' + elemSchools[s].name + '';
                            n++;
                            c3++;

                        } else if (n == 3) {

                            cFour += '
' + elemSchools[s].name + '';
                            n = 0;
