                    //--> School panel navigation
                    $('#cs-school-btn-wrapper button').click(function(){

                        var id = $(this).attr('id'),
                            panel = $(this).attr('aria-controls'),
                            activeP = $('.cs-school-btn.active').attr('aria-controls'),
                            activeT = $('.cs-school-btn.active').attr('id');


                        if(!$(this).hasClass('active')) {

                            $('#' + activeP).removeClass('active').attr('aria-hidden', 'true');
                            $('#' + activeP + ' a').attr('tabindex', '-1');
                            $('#' + activeT).removeClass('active');
                            $('.cs-school-btn').attr('tabindex', '-1');
                            $('#' + id).addClass('active').attr('tabindex', '0');
                            $('#' + panel).addClass('active').attr('aria-hidden', 'false');
                            $('#' + panel + ' a').attr('tabindex', '0');

                        }

                    });

                    //--> Keyboard navigation
                    $('.cs-school-btn').keydown(function(e){

                        var c = e.which,
                            panel = $(this).attr('aria-controls'),
                            link = $('#' + panel + ' a[data-link="1"][data-column="1"]');

                        if(c == KeyCodes.right || c == KeyCodes.down || c == KeyCodes.left || c == KeyCodes.up || c == KeyCodes.enter || c == KeyCodes.space) {

                            e.preventDefault();

                            if(c == KeyCodes.right) {
                                $(this).next().focus();
                            } else if (c == KeyCodes.down || c == KeyCodes.enter || c == KeyCodes.space) {
                                $(this).click();
                                link.delay(200).focus();
                            } else if(c == KeyCodes.left) {
                                $(this).prev().focus();
                            } else if(c == KeyCodes.up) {
                                $('#gb-school-btn').focus();
                            }

                        }

                        if(c == KeyCodes.esc) {
                            $('#gb-school-btn').focus();
                            $('nav, main, footer, .gb-header.left, #gb-translate, #gb-single-icon').attr('aria-hidden', 'false');
                            $('#gb-page').removeClass('cs-modal-open');
                            $('#gb-school-btn').removeClass('open');
                            $('#gb-school-btn').attr('aria-label', 'Open School List');
                            $('#cs-mega-schoollist-wrapper').attr('aria-hidden', 'true').fadeOut();
                        }

                    });

                    //--> Link navigation
                    $('.cs-school-wrapper a').keydown(function(e){

                        var k = e.which,
                            KeyCodes = _this.KeyCodes,
                            l = parseInt($(this).attr('data-link')),
                            c = parseInt($(this).attr('data-column'));

                        if(k == KeyCodes.down || k == KeyCodes.up || k == KeyCodes.left ||	k == KeyCodes.right) {

                            e.preventDefault();

                            if(k == KeyCodes.down && $('.cs-school-wrapper.active a[data-link="' + (l + 1) + '"][data-column="' + c + '"]').length == 0) {
                                $('.cs-school-wrapper.active a[data-link="' + 1 + '"][data-column="' + c + '"]').focus();
                            } else if(k == KeyCodes.down) {
                                $('.cs-school-wrapper.active a[data-link="' + (l + 1) + '"][data-column="' + c + '"]').focus();
                            } else if(k == KeyCodes.up && l == 1) {
                                $('.cs-school-btn.active').focus();
                            } else if(k == KeyCodes.up) {
                                $('.cs-school-wrapper.active a[data-link="' + (l - 1) + '"][data-column="' + c + '"]').focus();
                            } else if(k == KeyCodes.right && $('.cs-school-wrapper.active a[data-link="' + l + '"][data-column="' + (c + 1) + '"]').length == 0) {
                                $('.cs-school-wrapper.active a[data-link="' + l + '"][data-column="' + 1 + '"]').focus();
                            } else if(k == KeyCodes.right) {
                                $('.cs-school-wrapper.active a[data-link="' + l + '"][data-column="' + (c + 1) + '"]').focus();
                            } else if(k == KeyCodes.left && $('.cs-school-wrapper.active a[data-link="' + l + '"][data-column="' + (c - 1) + '"]').length == 0) {
                                $('.cs-school-wrapper.active a[data-link="' + l + '"][data-column="' + 4 + '"]').focus();
                            } else if(k == KeyCodes.left) {
                                $('.cs-school-wrapper.active a[data-link="' + l + '"][data-column="' + (c - 1) + '"]').focus();
                            }


                        }

                        if(k == KeyCodes.esc) {
                            $('#gb-school-btn').focus();
                            $('nav, main, footer, .gb-header.left, #gb-translate, #gb-single-icon').attr('aria-hidden', 'false');
                            $('#gb-page').removeClass('cs-modal-open');
                            $('#gb-school-btn').removeClass('open');
                            $('#gb-school-btn').attr('aria-label', 'Open School List');
                            $('#cs-mega-schoollist-wrapper').attr('aria-hidden', 'true').fadeOut();
                        }

                    });

                });


                //--> Mega School List Functionality
                $('#gb-school-btn').click(function(){

					var pageH = $('#gb-page').height();

                    if($('#cs-elem-school-btn').hasClass('active')) {
                    	$('#cs-elem-school-panel').attr('aria-hidden', 'false')
                    }
                    else {
                    	$('#cs-elem-school-panel').attr('aria-hidden', 'true')
                    }

                    if($(this).hasClass('open')) {

                        $('nav, main, footer, .gb-header.left, #gb-translate, #gb-single-icon').attr('aria-hidden', 'false');
                        $('#gb-page').removeClass('cs-modal-open');
                        $(this).removeClass('open');
                        $(this).attr('aria-label', 'Open School List');
                        $('#cs-mega-schoollist-wrapper').attr('aria-hidden', 'true').fadeOut();

                    } else {

                        $('nav, main, footer, .gb-header.left, #gb-translate, #gb-single-icon').attr('aria-hidden', 'true');
                        $('#gb-page').addClass('cs-modal-open');
                        $(this).addClass('open');
                        $(this).attr('aria-label', 'Close School List');
                        $('#cs-mega-schoollist-wrapper')
                        	.attr('aria-hidden', 'false')
                            .fadeIn(function(){

                            	$('button.cs-school-btn.active').focus();

                            });


                        $('#gb-page.cs-modal-open .sw-channel-item.home > a').unbind('focus').focus(function(){

                            if($('.cs-modal-open').length) {
                            	$('#gb-school-btn').focus();
							}
                        });


                        $('#cs-mega-schoollist-wrapper').unbind('click').click(function(){

                                $('nav, main, footer, .gb-header.left, #gb-translate, #gb-single-icon').attr('aria-hidden', 'false');
                                $('#gb-page').removeClass('cs-modal-open');
                                $('#gb-school-btn').removeClass('open');
                                $('#gb-school-btn').attr('aria-label', 'Open School List');
                                $(this).attr('aria-hidden', 'true').fadeOut();

                        });

                        $('#cs-mega-schoollist').unbind('click').click(function(){
                            return false;
                        });


                    }

                }).keydown(function(e){

                	var c = e.which;

                    if((c == KeyCodes.tab && $(this).hasClass('open')) || (c == KeyCodes.down && $(this).hasClass('open'))) {

                    	e.preventDefault();

                        $('button.cs-school-btn.active').focus();

                    }



                });



            }

        },

        "MyStart": function() {

        	// ADD TRANSLATE
        	$("#gb-translate-dropdown").creativeTranslate({
                "type": 1,

                "advancedOptions": {
                    "addMethod": "append",
                    "dropdownHandleText": "Translate",
                    "customElement": { // ONLY FOR FRAMESET AND API VERSIONS
                        "useCustomElement": true,
                        "translateItemsList": true, // true = THE TRANSLATE ITEMS WILL BE AN UNORDERED LIST, false = THE TRANSLATE ITEMS WILL JUST BE A COLLECTION OF  TAGS
                        "customElementMarkup": "[$CreativeTranslateListItems$]" // CUSTOM HTML MARKUP THAT MAKES THE CUSTOM TRANSLATE ELEMENT/STRUCTURE - USE [$CreativeTranslateListItems$] ACTIVE BLOCK IN THE MARKUP WHERE THE TRANSLATE ITEMS SHOULD BE ADDED
                	}
                }
            });

            $('.gb-mystart-dropdown .gb-dropdown ul').keydown(function(e) {
            	if (e.keyCode === 27) {
                	$('#gb-translate-dropdown').slideUp();
                    $('#gb-translate-btn').focus();
                }	


            });


            $(document).off("click", ".sw-mystart-dropdown");
            $(document).on("click", ".sw-mystart-dropdown", function(e) {
                $(".sw-dropdown", this).slideDown(300, "swing");
                $(this).addClass("open");
            });
            $(".sw-mystart-dropdown").hover(function(e) {
                e.preventDefault();
            }, function() {
                $(".sw-dropdown",this).slideUp(300, "swing").attr('aria-hidden', 'true');
                $(this).removeClass("open");
            });
            $(document).off("focus", ".sw-mystart-dropdown");
            $(document).on("focus", ".sw-mystart-dropdown", function(e) {
                $(".sw-dropdown", this).slideDown(300, "swing").attr('aria-hidden', 'false');
                $(this).addClass("open");
            });




            $('#gb-user-options-list a').attr('tabindex', '0');

        },

        "Header": function() {

        	//--> Skip Nav BTN
            $('#skipLink').focus(function(){
            	$('nav').css('top', '30px');
            }).blur(function(){
            	$('nav').css('top', '0px');
            });
            // ADD LOGO
            var showLogo = true;
            var logoSrc = $.trim("/cms/lib/NC01910393/Centricity/Template/GlobalAssets/images///Logos/GCS Logo 1.png");
            if(showLogo) {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").append('');
                } else {
                    $("#gb-logo").append('');
                }
            }

            // ADD SITENAME
            var siteNameOne = $.trim("");
            var siteNameTwo = $.trim("");
            if((siteNameOne != "") || (siteNameTwo != "")) {
                var splitLen = 2;
                var siteName = "Guilford County Schools";
                siteName = siteName.split(" ");
                var siteNameLength = siteName.length;
                if(siteNameLength > 2){
                    siteNameEnd = $.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                } else {
                    siteNameEnd = $.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                }
                siteNameBegin = $.trim(siteName.toString().replace(/,/g, " "));
                $("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
            } else if((siteNameOne != "") && (siteNameTwo == "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "");
            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameTwo + "");
            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
            }

            //--> Lets Talk Button
            if(false) {
            	$('#gb-lets-talk').html('Lets talk');
            } else {
            	$('#gb-lets-talk').remove();
            }

        },

        "ChannelBar": function() {
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){

                if($(this).hasClass('mega-channel')) {

                    var wW = $(window).width(),
                        drop = $('.sw-channel-dropdown', this).width(),
                        offset = $(this).offset();
                    //--> Center Dropdown
                    $('.sw-channel-dropdown', this).css('left', '-' + (offset.left - ((wW - drop) / 2)) + 'px');

                }
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });
            if('Yes' == 'Yes' || 'Yes' == 'Yes;No') {
            	$('.sw-channel-item').first().addClass('home');
                $('.sw-channel-item.home > a span').html('Home');
            	$('#gb-channel-list').addClass('using-home');
            }
        },

