            }

            var KeyCodes = this.KeyCodes;

            //--> Custom Site Shortcut App
            if($('#hp-custom-app .ui-widget.app.siteshortcuts').size() > 0) {
            	$('#hp-custom-app .siteshortcuts .ui-widget-detail').hide().attr('aria-hidden', 'true');
            	$('#hp-custom-app').show().attr('aria-hidden', 'false');
                $('#hp-custom-app .ui-widget-header').addClass('cs-fc cs-aic').attr('tabindex', '0');
                $('#hp-custom-app .ui-widget-header').append('');
            	$('#hp-custom-app .ui-widget-header').click(function(){

                	if($(this).hasClass('open')) {
                    	$('#hp-custom-app .ui-widget-detail').stop(true, true).slideUp().attr('aria-hidden', 'true');
                        $(this).removeClass('open');
                    } else {
                    	$('#hp-custom-app .ui-widget-detail').stop(true, true).slideDown().attr('aria-hidden', 'false');
                        $(this).addClass('open');
                    }

                }).keydown(function(e){

                	var c = e.which;

                    if(c == KeyCodes.down || c == KeyCodes.enter || c == KeyCodes.space ) {

                        e.preventDefault();
                    	$(this).click();
                        if($(this).hasClass('open')) {
                        	$('#hp-custom-app .ui-widget-detail a').first().focus();
                        }

                    }



                });
                $('#hp-custom-app .ui-widget-detail li a').keydown(function(e){

                	var c = e.which,
                    	next = $(this).parent().next().children('a'),
                        prev = $(this).parent().prev().children('a');

                   	if(c == KeyCodes.down || c == KeyCodes.up || c == KeyCodes.left || c == KeyCodes.right) {

                    	e.preventDefault();
                        if(c == KeyCodes.down && next.text() == "") {
                        	$('#hp-custom-app .ui-widget-header.open').focus().click();
                        } else if(c == KeyCodes.down) {
                        	next.focus();
                        } else if(c == KeyCodes.up && prev.text() == "") {
                        	$('#hp-custom-app .ui-widget-header.open').focus().click();
                        } else if(c == KeyCodes.up) {
                        	prev.focus();
                        } else if(c == KeyCodes.left) {
                        	$('#hp-custom-app .ui-widget-header.open').focus();
                        } else if(c == KeyCodes.right) {
                        	$('#hp-custom-app .ui-widget-header.open').click();
                            if($('#gb-lets-talk a').size()) {
                            	$('#gb-lets-talk a').focus();
                            } else {
                            	$('#gb-user-options a').focus();
                            }
                        }

                    }

                    if(c == KeyCodes.esc) {
                        $('#hp-custom-app .ui-widget-detail').slideUp();
                        $('#hp-custom-app .ui-widget-header').removeClass('open').focus();
                    }

                });
            }

            //--> Check Home Page Rows for HF app.

            $('.headlines').each(function(){

                    $('.headlines .ui-article-thumb').hover(function(){

                        $('.cs-image-more-text', this).stop(true, true).slideDown().attr('aria-hidden', 'false');

