                    }).focus(function(){

                        $('.cs-image-more-text', this).stop(true, true).slideDown().attr('aria-hidden', 'false');

                    }).blur(function(){

                        $('.cs-image-more-text', this).stop(true, true).slideUp().attr('aria-hidden', 'true');

                    });

                    $('.ui-article-thumb', this).each(function(){
                    	$(this).append('
