                    });
                    $('.ui-widget-detail', this).append($('.more-link', this));
                    $('a.more-link', this).html('VIEW ALL NEWS' + svg.more);
                    $('div.more-link-under').remove();

            });

            if($('.hp.row .headlines').length) {

                $('.hp.row .headlines').each(function(){

                	$('.ui-articles', this).addClass('cs-fc cs-fw');
                    $('.ui-articles > li ', this).addClass('cs-fc');

                });


            }

            if($('.hp.column .ui-article .twitter-timeline').length) {

            	$('.hp.column .ui-article').each(function(k, v){

