                    	$(v).addClass('has-iframe');

                    }

                });

            }

            var apps = 0;

            //--> Check if columns have apps.
            $('.hp.column').each(function(k, v){

            	if($('.ui-widget.app', v).length) {

                    apps++;

                } else {

                	$(v).remove();

                }

            });

            if(apps == 2) {

            	$('.bottom-row').addClass('two-columns');

            } else if(apps == 1) {

            	$('.bottom-row').addClass('one-column');

            } else if(apps == 0) {

            	$('.bottom-row').remove();

            }


        },



