        "Search": function() {

            //--> Setup Search
            $('#gb-search-outer form label').prepend($('#sw-mystart-search script'), $('#sw-search-input'));
            $('#sw-search-input').attr('placeholder', 'I am looking for...').attr('aria-hidden', 'true').attr('aria-labeledby', 'gb-search-lebel').attr('value', 'I am looking for...').hide();

            //--> Search Button
            $('#gb-search-btn').click(function(e){

            	e.preventDefault();

            	if($(this).hasClass('open')) {

            		$('#gb-search-outer #sw-search-input').attr('aria-hidden', 'true').removeClass('open');
                    $('#gb-search-outer #sw-search-input').hide("slide", {direction: "right"}, 300);
                    $('#gb-search-btn').attr('aria-label', 'Open Search');
                	$(this).removeClass('open');

                } else {

            		$('#gb-search-outer #sw-search-input').attr('aria-hidden', 'false').addClass('open');
                    $('#gb-search-outer #sw-search-input').show('slide', {direction:"right"}, 300, function(){
                    	$('#gb-search-outer #sw-search-input').focus();
                    });
                    $('#gb-search-btn').attr('aria-label', 'Close Search');
                	$(this).addClass('open');

                }

            });


        },

        "CalendarListView": function() {
            if($(".ui-widget.app.calendar").length) {
                // AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
                if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
                    $("a.ui-btn-toggle.list").click();
                    $("a.ui-btn-toggle.month").click();
                    $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
                }

                switch(this.GetBreakPoint()) {
                    case "desktop": case "768": case "640":
                        $(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
                    break;
                    case "480": case "320":
                        $(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
                        $(".ui-btn-toggle.month").removeClass("active");
                        $(".ui-btn-toggle.list").addClass("active");
                    break;
                }
            }
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }

    };

    function eventsByDay(container) {
        var countLi = 0;
        var csMonth = "";
        $(".ui-article", container).each(function(){
        	if($(this).closest('.hp.row')) {
            	$(this).addClass('cs-fc');
            }
            if (!$(".sw-calendar-block-date", this).length){
                var moveArticle = $(this).html();
                $(this).parent().prev().children().children().next().append(moveArticle);
                $(this).parent().remove();
            } else {
                $(".sw-calendar-block-date", this).wrapInner('');
                $(".joel-month", this).parents(".sw-calendar-block-date").prepend($(".joel-month", this));
            }

			//add an ALL DAY label if no time was given
			$('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
					$(this).prepend('All Day');
				}
			});

        });

    }

