    $("ul#socialTabs li").click(function(e){
        e.preventDefault();
        if (!$(this).hasClass("active")) {
            var tabNum = $(this).index();
            var nthChild = tabNum+1;
            $("ul#socialTabs li.active").removeClass("active");
            $(this).addClass("active");
            $("ul#socialTab li.active").removeClass("active");
            $("ul#socialTab li:nth-child("+nthChild+")").addClass("active");
        }
