function fb_tabs(selectedtab) {    

    var s_fb_tab_content = "fb_tab_content_" + selectedtab;   
    var contents = document.getElementsByTagName("div");
    for(var x=0; x<contents.length; x++) {
        name = contents[x].getAttribute("name");
        if (name == 'fb_tab_content') {
            if (contents[x].id == s_fb_tab_content) {
            contents[x].style.opacity = "1"; 
      contents[x].style.display = "block"; 
            } else {
            contents[x].style.opacity = "0";
      contents[x].style.display = "none"; 
            }
        }
    }   

    var s_fb_tab = "fb_tab_" + selectedtab;       
    var fb_tab = document.getElementsByTagName("a");
    for(var x=0; x<fb_tab.length; x++) {
        name = fb_tab[x].getAttribute("name");
        if (name == 'fb_tab') {
            if (fb_tab[x].id == s_fb_tab) {
            fb_tab[x].className = "active";                       
            } else {
            fb_tab[x].className = "";
            }
        }
    }     
}

