div.script:
$(document).ready(function () {
    $("#1e935bcf-78af-4aee-90d1-6086dd7dbc0f").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 5 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#4e8e9392-aac3-4558-80d4-d28a308d00fd").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#4e8e9392-aac3-4558-80d4-d28a308d00fd').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#4e8e9392-aac3-4558-80d4-d28a308d00fd').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 4 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#6f297b39-cd15-417b-a274-d378330173fc').hide();
    jQuery('#6f297b39-cd15-417b-a274-d378330173fc').listSolrResults({
        currentPagePath: '/content/schools/ncva/en',
        label: 'News',
        listFrom: 'solr',
        limit: '5',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/content/schools/ncva/en/news.html',
        viewAllTitle: '',
        uuid: '6f297b39-cd15-417b-a274-d378330173fc',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

