$('.news-slider').bxSlider({
	auto: true,
	autoHover: true,
	adaptiveHeight: true,
	onSliderLoad: function(currIndex) {
		var newsheight = $(".news:nth-child(" + (currIndex + 2) + ") .right-content").height() + 20;
		$(".main .col-item.news-wrapper .bx-wrapper .bx-pager").css({
			bottom: newsheight
		});
		$(window).resize(function() {
		    var newsheight = $(".news:nth-child(" + (currIndex + 2) + ") .right-content").height() + 20;
        		$(".main .col-item.news-wrapper .bx-wrapper .bx-pager").css({
        			bottom: newsheight
        		});

		});
	},
	onSlideBefore: function(currentSlideNumber, totalSlideQty, currentSlideHtmlObject) {
		var newsheight = $(".news .right-content").eq(currentSlideHtmlObject + 1).height() + 20;
		$(".main .col-item.news-wrapper .bx-wrapper .bx-pager").css({
			bottom: newsheight
		});
		$(window).resize(function() {
		    var newsheight = $(".news .right-content").eq(currentSlideHtmlObject + 1).height() + 20;
		$(".main .col-item.news-wrapper .bx-wrapper .bx-pager").css({
			bottom: newsheight
		});
		});
	}
});

