    $(document).ready(function () {
        if ($("body").css("background-color").includes("rgba(")) {
            var cc = $("body").css("background-color").replace("rgba(", "");
            cc = cc.replace(")", "");
            ccolors = cc.split(", ");
            var cA = parseInt(ccolors[3]);
            if (cA < 0.5) {
                $(body).css("background-color", "white");
            }
        }

        $.each($("body > form *"), function (index, value) {
            if ($(value).parents("header .Bottom").length != 1 && $(value).parents("svg").length != 1 && $(value).css("display") != "none") {
                colorValues(value);
            }
        });

        $.each($("img"), function (index, value) {
            if ($(value).attr("alt") == null || $(value).attr("alt") == "") {
                $(value).attr("alt", $(value).attr("src"));
            }
        });

        $.each($("a"), function (index, value) {
            if ($(value).text().replace(/ /g, "").replace(/\r?\n|\r/g, "") == "" && ($(value).attr("aria-label") == null || $(value).attr("aria-label") == "")) {
                $(value).attr("aria-label", $(value).attr("href"));
            }
        });
        $.each($("input"), function (index, value) {
            if ($(value).attr("aria-label") == null || $(value).attr("aria-label") == "") {
                $(value).attr("aria-label", $(value).attr("id"));
            }
        });
    });

