var thumbVideo = document.createElement("video");
function setThumbVid() {
    var hpVideo = document.getElementById("hpVideo");
    thumbVideo.src = hpVideo.src;
}
setThumbVid();
document.getElementById('videoPosition').addEventListener('mousemove', function (e) {
    var valueHover = calcSliderPos(e).toFixed(2) * thumbVideo.duration;
    var canvas = document.createElement('canvas');
    canvas.width = 64 * 2;
    canvas.height = 48 * 2;
    var ctx = canvas.getContext('2d');
    thumbVideo.currentTime = valueHover;
    $("#hpPosThumb").css("left", (e.target.offsetLeft + e.offsetX - 64) + "px");
    setTimeout(function () {
        ctx.drawImage(thumbVideo, 0, 0, canvas.width, canvas.height);
        var dataURI = canvas.toDataURL('image/jpeg');
        if (!dataURI.includes("AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA")) {
            $("#hpPosThumb").attr("src", dataURI);
            $("#hpPosThumb").attr("data-source", "Found");
        }
    }, 1);
}); 
function calcSliderPos(e) {
    return (e.offsetX / e.target.clientWidth);
}
$("#videoPosition").hover(
    function () {
        $("#hpPosThumb").css("display", "block");
        if ($("#hpPosThumb").attr("data-source") != "Found"){
            $("#hpPosThumb").css("display", "none");
        }
    }, function () {
        $("#hpPosThumb").css("display", "none");
    }
);

