function SmoothScroll() {
    var height = $('.slideshow_outer_container').outerHeight();
    var topper = $('.slideshow_outer_container').position().top;
    window.scroll({
        top: height + topper,
        left: 0,
        behavior: 'smooth'
    });
}

setInterval(function () {
    if (window.innerWidth < 991) {
        var top = $("header").position().top;
        var headerheight = $("header").outerHeight();
        var windowheight = window.innerHeight;
        $(".homepage_news_container").css("margin-top", windowheight - (top + headerheight) + "px");
    }
    else {
        $(".homepage_news_container").css("margin-top", "");
    }
}, 1)
