div.script:
$(document).ready(function () {
    $("#cddbcd0e-9c1f-4521-b682-755fa9ffab57").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 6000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 8 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#ce35e41c-e997-4eb7-83bd-c31c7c1bd115").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#ce35e41c-e997-4eb7-83bd-c31c7c1bd115').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#ce35e41c-e997-4eb7-83bd-c31c7c1bd115').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 10000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 8 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#29f4ab0c-7c33-42bc-92e6-a8cc15e0cbfd').hide();
    jQuery('#29f4ab0c-7c33-42bc-92e6-a8cc15e0cbfd').listSolrResults({
        currentPagePath: '/content/schools/ohva/en',
        label: 'News',
        listFrom: 'solr',
        limit: '3',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: '29f4ab0c-7c33-42bc-92e6-a8cc15e0cbfd',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

