$(document).ready(function () {
    $.each($(".hpDivU .slick-slide"), function (key, value) {
        if (window.innerWidth >= 991) {
            $(value).find(".ArticleBody").css("margin-top", "");
            $(value).find(".HPNewsPhoto").css("margin-top", "");
            var h1 = $(value).find(".HPNewsPhoto").outerHeight();
            var h2 = $(value).find(".ArticleBody").outerHeight();
            if (h1 > h2) {
                $(value).find(".ArticleBody").css("margin-top", ((h1 - h2) / 2).toString() + "px");
            }
            else {
                $(value).find(".HPNewsPhoto").css("margin-top", ((h2 - h1) / 2).toString() + "px");
            }
            var x = value;
            setTimeout(function () {
                $(value).find(".ArticleBody").css("margin-top", "");
                $(value).find(".HPNewsPhoto").css("margin-top", "");
                var h1 = $(value).find(".HPNewsPhoto").outerHeight();
                var h2 = $(value).find(".ArticleBody").outerHeight();
                if (h1 > h2) {
                    $(value).find(".ArticleBody").css("margin-top", ((h1 - h2) / 2).toString() + "px");
                }
                else {
                    $(value).find(".HPNewsPhoto").css("margin-top", ((h2 - h1) / 2).toString() + "px");
                }
            }, 250);
        }
    });
});
