div.script:
$(document).ready(function () {
    $("#ed59ad92-d7f9-4f7c-b3da-f6b9b74abca6").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 4 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#0d2e62c2-1b35-4b3a-b6a7-125fccf61fe6").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#0d2e62c2-1b35-4b3a-b6a7-125fccf61fe6').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#0d2e62c2-1b35-4b3a-b6a7-125fccf61fe6').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 6 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#6b86ce92-98de-46de-942e-83fa6fb9e18d').hide();
    jQuery('#6b86ce92-98de-46de-942e-83fa6fb9e18d').listSolrResults({
        currentPagePath: '/content/schools/ovca/en',
        label: 'News',
        listFrom: 'solr',
        limit: '3',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: '6b86ce92-98de-46de-942e-83fa6fb9e18d',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

