    setInterval(function () {
        var h1 = $(".hpDiv3 .hpText").outerHeight();
        var h2 = $(".hpDiv3 .hpTextImage").outerHeight();
        if (h2 > h1) {
            $(".hpDiv3 .hpText").css("top", ((h2 - h1) / 2).toString() + "px");
            $(".hpDiv3 .hpTextImage").css("top", "");
        } else {
            $(".hpDiv3 .hpTextImage").css("top", ((h1 - h2) / 2).toString() + "px");
            $(".hpDiv3 .hpText").css("top", "");
        }

    }, 1);

