        window.resizeMiniCalendar20200419045731919 = function () {
        var calId = 'miniCal20200419045731919';
        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200419045731919';
        var calMonthFullId = 'miniCalMonthFull20200419045731919';
        var calMonthShortId = 'miniCalMonthShort20200419045731919';
        var calGoToCalFullId = 'miniCalGotToCalFull20200419045731919';
        var calGoToCalShortId = 'miniCalGoToCalShort20200419045731919';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

