$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=YICEaNVQ2hsSVfG%2bUHprRQ%3d%3d&SessionID=06fe71c3-0bae-424c-bd71-2e67452aeeba&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f19%2f2020+4%3a59%3a24+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.6.252';
