var LTIE9;
function drawCurve() {
	// #hp-header-container -- IF #hp-header-container THEN ADD initBottomOverlay() AND initTopOverlay()
	if ($("#hp-header-container").length > 0){
		initBottomOverlay();
		initTopOverlay();
      	}
}
function initTopOverlay() {
	var canvas = document.getElementById("top-photo-overlay");

	//IF IE
	if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas);}

	var ctx = canvas.getContext("2d");
	drawTop(ctx);
}
function drawTop(ctx) {
	// layer1/Group
	ctx.save();

	// layer1/Group/Path
	ctx.save();
	ctx.beginPath();
	ctx.moveTo(640.0, 15.0);
	ctx.lineTo(841.0, 0.0);
	ctx.lineTo(640.0, 0.0);
	ctx.lineTo(640.0, 15.0);
	ctx.closePath();
	ctx.fillStyle = "#70000C";
	ctx.fill();

	// layer1/Group/Path
	ctx.beginPath();
	ctx.moveTo(201.0, 15.0);
	ctx.lineTo(0.0, 0.0);
	ctx.lineTo(201.0, 0.0);
	ctx.lineTo(201.0, 15.0);
	ctx.closePath();
	ctx.fill();
	ctx.restore();
	ctx.restore();
}

function initBottomOverlay() {
	var canvas = document.getElementById("bottom-photo-overlay");

	//IF IE
	if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas);}

	var ctx = canvas.getContext("2d");
	drawBottom(ctx);
}
function drawBottom(ctx) {
	// layer1/Group
	ctx.save();

	// layer1/Group/Path
	ctx.save();
	ctx.beginPath();
	ctx.moveTo(201.0, 0.0);
	ctx.lineTo(0.0, 15.0);
	ctx.lineTo(201.0, 15.0);
	ctx.lineTo(201.0, 0.0);
	ctx.closePath();
	ctx.fillStyle = "#EAEAEA";
	ctx.fill();

	// layer1/Group/Path
	ctx.beginPath();
	ctx.moveTo(640.0, 0.0);
	ctx.lineTo(841.0, 15.0);
	ctx.lineTo(640.0, 15.0);
	ctx.lineTo(640.0, 0.0);
	ctx.closePath();
	ctx.fill();
}

