	function globalIcons() {
		var iconSize = 30;
		var numOfIcons = 4;
		var iconColor = '#FFFFFF';
		var iconControl;
		var iconLink;
		var iconTarget;
		var iconText;
		for(i=1; i<=numOfIcons; i++){
			//Grab The Icons Details		
			switch(i){
				case 1:
					iconControl = 'Communication/18.png';
					iconLink = 'https://www.pgsd.org/Domain/750';
					iconTarget = '_blank';
					iconText = 'E-Mail';
				break;
				case 2:
					iconControl = '/cms/lib/PA01916597/Centricity/Template/GlobalAssets/icons/bwPicon-trials.png';
					iconLink = 'http://powerschool.pgsd.org/public';
					iconTarget = '_blank';
					iconText = 'PowerSchool';
				break;
				case 3:
					iconControl = 'Student Program/5.png';
					iconLink = 'http://pgsd.nutrislice.com';
					iconTarget = '_self';
					iconText = 'Lunch Menus';
				break;
				case 4:
					iconControl = '/cms/lib/PA01916597/Centricity/Template/GlobalAssets/icons/Final-Schoology-icon.png';
					iconLink = 'https://onlinelearning.pgsd.org';
					iconTarget = '_blank';
					iconText = 'Schoology';
				break;
			}


			// DETERMINE IF THIS IS A SYSTEM ICON OR IMAGE
			var myIconType = iconControl.split("/");
			if($.inArray("cms", myIconType) != -1) {
				myIconType = "Image";
			} else {
				myIconType = "System";
			}	

			// CHECK FOR SITEALIAS STRING
			iconLink = iconLink.replace("[SITEALIAS]","pgsd");

			// CHECK FOR GLOBAL CALENDAR LINK
			iconLink = iconLink.replace("[SITECALENDARLINK]","/Page/2");

			// CHECK FOR GLOBAL SITEMAP LINK
			iconLink = iconLink.replace("[SITEMAPLINK]","/site/Default.aspx?PageType=15&SiteID=4&SectionMax=15&DirectoryType=6");

			if(myIconType == "System") {
				// ADD THE  GLOBAL ICON ELEMENT
				$("#gb-global-icons").append("" + iconText + "");
				// DRAW THE ICON
				drawIcons(i, iconControl, iconColor, iconSize);
			} else {
				// ADD THE GLOBAL IMAGE(ICON) ELEMENT
				$("#gb-global-icons").append("" + iconText + "");		
			}

			// SET POSITIONING AND SIZE BASED OF THE ICON SIZE
			var iconPositionLeft = (80 - iconSize) / 2 + "px";
			var iconPositionTop = (50 - iconSize) / 2 + "px";
			var iconPositionWidth = iconSize + "px";
			var iconPositionHeight = iconSize + "px";
			$("a.global-icon .global-image").css({
				'left'		: iconPositionLeft,
				'top'		: iconPositionTop,
				'width'		: iconPositionWidth,
				'height'	: iconPositionHeight
			})
		}
	}
	function useChannelForSection() {
		//DOC .sp.column.one .ui-widget-header h1 -- SET USE ChannelName FOR THE PAGELISTNAVIGATION HEADER IF ONE IS NOT PRESENT
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
	}
