var dimages=new Array();
var numImages=9;
dimages[0]=new Image();
dimages[0].src="resources/slideshow/happyFeet01.jpg";

dimages[1]=new Image();
dimages[1].src="resources/slideshow/happyFeet02.jpg";

dimages[2]=new Image();
dimages[2].src="resources/slideshow/happyFeet03.jpg";

dimages[3]=new Image();
dimages[3].src="resources/slideshow/happyFeet04.jpg";

dimages[4]=new Image();
dimages[4].src="resources/slideshow/happyFeet05.jpg";

dimages[5]=new Image();
dimages[5].src="resources/slideshow/happyFeet06.jpg";

dimages[6]=new Image();
dimages[6].src="resources/slideshow/happyFeet07.jpg";

dimages[7]=new Image();
dimages[7].src="resources/slideshow/happyFeet08.jpg";

dimages[8]=new Image();
dimages[8].src="resources/slideshow/happyFeet09.jpg";

var curImage=-1;

function swapPicture()
{
  if (document.images)
  {
    var nextImage=curImage+1;
    if (nextImage>=numImages)
      nextImage=0;
    if (dimages[nextImage] && dimages[nextImage].complete)
    {
      var target=0;
      if (document.images.myImage)
        target=document.images.myImage;
      if (document.all && document.getElementById("myImage"))
        target=document.getElementById("myImage");
  
      // make sure target is valid.  It might not be valid
      //   if the page has not finished loading
      if (target)
      {
        target.src=dimages[nextImage].src;
        curImage=nextImage;
      }
      setTimeout("swapPicture()", 5000);
    }
    else
    {
      setTimeout("swapPicture()", 5000);
    }
  }
}
setTimeout("swapPicture()", 5000);
