$(document).ready(function(){
  setTimeout(function(){
  $('#upcoming-events .summary-item .summary-metadata-item--date').each(function() {
      var currentYear = (new Date).getFullYear();
      var eventString = $(this).text();
      var eventYear = ', ' + currentYear;
      var noRange = eventString.replace(eventYear, '');
      var noYear = noRange.replace(eventYear, '');
      eventString = $(this).text(noYear);

      if ( $(this).text().indexOf('–') >= 0 ) {
        $(this).addClass('date--range');
      }
    });
  },500);
});
