jQuery(document).ready(function($){

    jQuery('#wpcs_tab_3554').click(function($){

        if( ! (jQuery('#wpcs_content_main_3554').hasClass('is_open')) ){

            // Open slider
            wpcs_open_slider_3554();

        } else {

            // close slider
            wpcs_close_slider_3554();

        }

    });

    jQuery("#wpcs_overlay_3554, #wpcs_close_slider_3554").click(function(){
        wpcs_close_slider_3554();
    });


});

function wpcs_open_slider_3554(do_repeat){

    do_repeat = typeof do_repeat !== 'undefined' ? do_repeat : 0 ;

    if( do_repeat !== 0 ){
        jQuery('#wpcs_content_main_3554').addClass('do_repeat');
        jQuery( "#wpcs_content_main_3554" ).data( "interval", do_repeat );
    }

    if( ! (jQuery('#wpcs_content_main_3554').hasClass('is_open')) && !(jQuery('#wpcs_content_main_3554').hasClass('is_opening')) ){

        // hide tap
        jQuery('#wpcs_tab_3554,.wpcs_tab').fadeTo("slow", 0);

        jQuery('#wpcs_content_main_3554').addClass('is_opening');

        jQuery("#wpcs_overlay_3554").addClass('wpcs_overlay_display_cross');

        jQuery( "#wpcs_overlay_3554").fadeIn('fast');

        // PRO FEATURE - PUSH BODY

        jQuery('#wpcs_content_main_3554').addClass('is_open');

        jQuery( "#wpcs_content_main_3554" ).animate({
            opacity: 1,
        right: "+=500"
    }, 250 , function() {

            // hide tap
            jQuery('#wpcs_tab_3554,.wpcs_tab').fadeTo("slow", 0);

            // Trigger some thing here once completely open
            jQuery( "#wpcs_content_inner_3554").fadeTo("slow" , 1);

            // Remove is_opening class
            jQuery('#wpcs_content_main_3554').removeClass('is_opening');

        });

    }

}

function wpcs_close_slider_3554(){

    if( (jQuery('#wpcs_content_main_3554').hasClass('is_open')) && !(jQuery('#wpcs_content_main_3554').hasClass('is_closing')) ) {

        jQuery("#wpcs_overlay_3554").removeClass('wpcs_overlay_display_cross');

        jQuery('#wpcs_content_main_3554').addClass('is_closing');

        jQuery("#wpcs_content_main_3554").animate({
        right:
        "-=500"
    }
    , 250 ,
        function () {

            // Trigger some thing here once completely close
            jQuery("#wpcs_content_main_3554").fadeTo("fast", 0);
            jQuery("#wpcs_content_inner_3554").slideUp('fast');
            jQuery("#wpcs_overlay_3554").fadeOut('slow');
            jQuery('body').removeClass('fixed-body');

            //  Removing is_open class in the end to avoid any confliction
            jQuery('#wpcs_content_main_3554').removeClass('is_open');
            jQuery('#wpcs_content_main_3554').removeClass('is_closing');


            // display tap
            jQuery('#wpcs_tab_3554,.wpcs_tab').fadeTo("slow", 1);

        });

        if( (jQuery('#wpcs_content_main_3554').hasClass('do_repeat')) ) {
            setTimeout(function () {
                wpcs_open_slider_3554(0);
            }, 0 );
        }

    }

}

