function SubmitForm_ContentPlaceHolder4_ContentRepeater4_ctl00_6(i) {
    if (Page_ClientValidate("group13349" + i)) {
        var formdata = [];
         formdata.push(new nseFormData('ContentPlaceHolder4_ContentRepeater4_ctl00_6_ctl01_6_ContentRepeater1_6_ctl00_0_txtText_0', '', 13351, 1));  formdata.push(new nseFormData('ContentPlaceHolder4_ContentRepeater4_ctl00_6_ctl01_6_ContentRepeater1_6_ctl00_1_txtText_1', 'ContentPlaceHolder4_ContentRepeater4_ctl00_6_ctl01_6_ContentRepeater1_6_ctl00_1_txtText2_1', 13362, 3)); 
        if (formdata.length > 0) {
            for (var i = 0; i < formdata.length; i++) {
                var f = formdata[i];
                if (f.Type == 1 || f.Type == 2 || f.Type == 3 || f.Type == 4) {
                    f.Value = document.getElementById(f.ClientID).value;
                }
                else if (f.Type == 5 || f.Type == 6) {
                    var v = "";
                    var o = document.getElementById(f.ClientID);
                    var oo = o.getElementsByTagName("input");;
                    for (var j = 0; j < oo.length; j++) {
                        if (oo[j].checked)
                            v = v + oo[j].value + ",";
                    }
                    f.Value = v;
                }
            }
        }
        nse5._0.Service.SubmitForm(1, 13349, 0, formdata, FormSuccess_ContentPlaceHolder4_ContentRepeater4_ctl00_6)
    }
}
