        ResetForm_ContentPlaceHolder4_ContentRepeater4_ctl00_6(); OpenConfirmMessage('Thank you for signing up for CISD emails and newsletters.  ');
    }
}
function ResetForm_ContentPlaceHolder4_ContentRepeater4_ctl00_6() {
    var tabStrip = $find("ctl00_ContentPlaceHolder4_ContentRepeater4_ctl06_ctl00_RadTabStrip1");
    var tabs = tabStrip.get_tabs();
    tabs.getTab(0).select();
    var formdata = [];
     formdata.push(new nseFormData('ContentPlaceHolder4_ContentRepeater4_ctl00_6_ctl01_6_ContentRepeater1_6_ctl00_0_txtText_0', '', 13351, 1));  formdata.push(new nseFormData('ContentPlaceHolder4_ContentRepeater4_ctl00_6_ctl01_6_ContentRepeater1_6_ctl00_1_txtText_1', 'ContentPlaceHolder4_ContentRepeater4_ctl00_6_ctl01_6_ContentRepeater1_6_ctl00_1_txtText2_1', 13362, 3)); 
    if (formdata.length > 0) {
        for (var i = 0; i < formdata.length; i++) {
            var f = formdata[i];
            if (f.Type == 1 || f.Type == 2 || f.Type == 4) {
                document.getElementById(f.ClientID).value = "";
            }
            else if (f.Type == 3) {
                document.getElementById(f.ClientID).value = "";
                var c2 = document.getElementById(f.ClientID2);
                if (c2)
                    c2.value = "";
            }
            else if (f.Type == 5 || f.Type == 6) {
                var o = document.getElementById(f.ClientID);
                var oo = o.getElementsByTagName("input");;
                for (var j = 0; j < oo.length; j++) {
                    oo[j].checked = false;
                }
            }
        }
    }
    document.getElementById("divConfirmMessage").innerHTML = document.getElementById("divConfirmOriginal").innerHTML;
}

