var flexipay = {
  runFlexipay: function runFlexipay() {
    var interval = setInterval(function () {
      var count = 0;
      var maxCount = 500;
      var flexiTerms = document.querySelector('.footer .tsAndCs');

      if (flexiTerms !== null && flexiTerms !== undefined) {
        clearInterval(interval);
        document.querySelector('.footer .tsAndCs').addEventListener('click', function (e) {
          var target = e.currentTarget;
          var arrow = target.querySelector('.arrow');
          var container = document.querySelector('.tsAndCs-contaner');

          if (arrow.className.indexOf('closed') > -1) {
            arrow.className = arrow.className.replace('closed', '');
            container.className = container.className.replace('closed', '');
          } else {
            arrow.className += ' closed';
            container.className += ' closed';
          }
        });
      }

      if (count > maxCount) {
        clearInterval(interval);
      }

      count++;
    }, 100);
    var header = document.getElementsByClassName("bar")[0];
    var sticky = header.offsetTop + 100;
    window.addEventListener('scroll', function () {
      var headerHeight = 0;

      var nonDepHeader = document.querySelector('#staticHeader');

      if (nonDepHeader === undefined || nonDepHeader === null) {
        if (window.outerWidth > 599) {
          headerHeight = document.querySelector('.sticky .main-header').clientHeight + document.querySelector('.sticky .navigation').clientHeight;
        } else {
          headerHeight = document.querySelector('.sticky-wrapper .main-header').clientHeight;
        }
      } else {
        headerHeight = nonDepHeader.clientHeight;
      }

      var stickyBar = document.querySelector('.flexipay .header .bar');

      if (window.pageYOffset > sticky) {
        header.classList.add("sticky");
        stickyBar.style.top = headerHeight + "px";
      } else {
        header.classList.remove("sticky");
        stickyBar.style.top = "initial";
      }
    });
  }
};
window.addEventListener("load", function () {
  if (document.querySelectorAll("#flexipay").length) {
    flexipay.runFlexipay();
  }
});
