var sizeGuides = {
  sizeGuidePortalFunc: function sizeGuidePortalFunc() {
    console.log('size gyide');
    var sizeGuidePortal = {};

    sizeGuidePortal.toggleAccordion = function (target) {
      var target = target.currentTarget; 

      var targetList = target.querySelector('ul');

      if (targetList.className.indexOf('open') === -1) {
        target.classList.add('active');
        targetList.classList.add('open');
      } else {
        target.classList.remove('active');
        targetList.classList.remove('open');
      }
    };

    sizeGuidePortal.toggleOptions = function (target, device) {
      var target = target.currentTarget;
      var parent = target.parentNode;

      if (parent.className.indexOf('active') === -1) {
        var parentClass = parent.className.replace('active', '').trim(); 

        var dropMenus = document.querySelectorAll('#drop_menu > ul li > div');

        for (var p = 0; p < dropMenus.length; p++) {
          dropMenus[p].classList.remove('show');
        }

        var sizeGuideId = parent.getAttribute('data-slide-id');
        var sizeGuideSection = document.querySelector('#' + sizeGuideId);
        sizeGuideSection.classList.add('show');
        var container = document.querySelector('#drop_menu > ul');
        container.className = sizeGuideId; 

        var desktopTabs = document.querySelectorAll('#size-guide-tabs-desktop .header-menu a');
        var mobileTabs = document.querySelectorAll('#size-guide-tabs-mobile a');

        for (var n = 0; n < desktopTabs.length; n++) {
          desktopTabs[n].classList.remove('active');
          mobileTabs[n].classList.remove('active');
        } 


        var sections = document.querySelectorAll('.' + parentClass);

        for (var i = 0; i < sections.length; i++) {
          sections[i].classList.add('active');
        }
      } 

    };

    sizeGuidePortal.addEventListeners = function () {
      var items = document.querySelectorAll('.has-sub');

      for (var i = 0; i < items.length; i++) {
        items[i].addEventListener('click', function (e) {
          sizeGuidePortal.toggleAccordion(e);
        });
      } 


      var desktopSizeGuideOptions = document.querySelectorAll('#size-guide-tabs-desktop span');

      for (var x = 0; x < desktopSizeGuideOptions.length; x++) {
        desktopSizeGuideOptions[x].addEventListener('click', function (e) {
          sizeGuidePortal.toggleOptions(e, 'Desktop');
        });
      } 


      var mobileSizeGuideOptions = document.querySelectorAll('#size-guide-tabs-mobile ul a span');

      for (var x = 0; x < mobileSizeGuideOptions.length; x++) {
        mobileSizeGuideOptions[x].addEventListener('click', function (e) {
          sizeGuidePortal.toggleOptions(e, 'Mobile');
        });
      }
    };

    sizeGuidePortal.pollForElements = function (els, cb) {
      for (var i = 0; i < els.length; i++) {
        if (!sizeGuidePortal.pollForSingleElement(els[i]) && sizeGuidePortal.pollForSingleElement(els[i]) !== undefined) {
          return false;
        }
      }

      cb();
    };

    sizeGuidePortal.pollForSingleElement = function (selector) {
      var count = 0;
      var maxCount = 100;
      var delay = 50;
      var el;
      var interval = setInterval(function () {
        el = document.querySelectorAll(selector);

        if (el.length > 0) {
          clearInterval(interval);
          return true;
        }

        if (count > maxCount) {
          clearInterval(interval);
          return false;
        }

        count++;
      }, delay);
    };

    sizeGuidePortal.pollForElements(['.has-sub', '#size-guide-tabs-desktop .header-menu span', '#size-guide-tabs-mobile ul a span'], sizeGuidePortal.addEventListeners);
  }
};
