        "Search": function() {
        	var _this = this;

        	$(document).on("click keydown", "#gb-search-toggle", function(e) {
            	if(_this.AllyClick(e)) {
                	e.preventDefault();

                    if($(this).attr("aria-expanded") == "false") {
                        $(this).attr("aria-expanded", "true");

                        $("#gb-search-outer").slideDown(function() {
							$("#gb-search-input").focus();
                        }).attr("aria-hidden", "false");
                    } else {
                        $(this).attr("aria-expanded", "false");

                        $("#gb-search-outer").slideUp().attr("aria-hidden", "true");
                    }
                }
            });

            $(document).on("keyup", function() {
            	if($("#gb-search-outer").attr("aria-hidden") == "false" && !$("#gb-search-outer").find(":focus").length && !$("#gb-search-toggle").is(":focus")) {
                	$("#gb-search-toggle").attr("aria-expanded", "false");
                    $("#gb-search-outer").slideUp().attr("aria-hidden", "true");
                }
            });

            $("#gb-search-button").click(function(e){
                if(_this.AllyClick(e)) {
                    e.preventDefault();

                    if($.trim($("#gb-search-input").val()) != "") {
                    	window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                    } else {
                    	$("#gb-search-input").addClass("search-empty");
                    }
                }
            });

            $("#gb-search-form").submit(function(e){
                e.preventDefault();

                if($.trim($("#gb-search-input").val()) != "") {
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                } else {
                    $("#gb-search-input").addClass("search-empty");
                }
            });

            $("#gb-search-input").focus(function() {
            	$(this).removeClass("search-empty");
            });
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

