$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=C81StPfGiY5nAZ9FNFstkg%3d%3d&SessionID=28f0a0e4-106e-4bbd-9fa3-aeb4333355e2&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f19%2f2020+4%3a31%3a41+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=216.213.255.230';
