function RefreshPage()

{
countDownTime--;
if (countDownTime <=0)
{
countDownTime=countDownInterval;
clearTimeout(timerrefresh)
window.location.reload()
return
}

timerrefresh=setTimeout("RefreshPage()", 1000);
}



function MakeTransition(current,next)
 {
    
    
    if(opacity==0)
    {
	opacity = 100;
	timerx=setTimeout("MakeTransition(" + current + "," + next + ")",900);
    }
    else
    {
    	timerx=setTimeout("MakeTransition(" + current + "," + next + ")",40);
    }
    
    opacity -= 5;
    document.getElementById("resim" + current).style.filter = "alpha(opacity:" + opacity + ")";
    document.getElementById("resim" + next).style.filter = "alpha(opacity:" + (100-opacity) + ")";   
    if (opacity==0) clearTimeout(timerx);
}






function transition(cur)
 {
    var nxt;
    
    	if (cur==0) 
		{
			cur=2;
			nxt=1;
		}
		else nxt=cur+1;
		if (nxt==3) nxt=1;	

    MakeTransition(cur,nxt);
   
}





function PauseSlideShow()
	{
	clearTimeout(timer);
	clearTimeout(timerx);
	clearTimeout(timerbutton);
	clearTimeout(timerrefresh);
	}



function StartSlideShow()
{
opacity=0;
timer=setTimeout('SlideShow()',PauseTime);
}



function SlideShow()
{
	
	ChangeButton();
	y++;
	if(y==3) y=1;
	StartSlideShow();
	transition(y-1);
			
}


function ChangeButton()

{
	buttonnumber = y;
	
	document.getElementById(PictureLinkID).href="?rightframe=/" + NewsFolder + "/" + buttonnumber + NewsWebType;
		
	
	for(var i=1;i<=2;i++)
	{
		document.getElementById(TitleMainName + i).style.display = buttonnumber==i?"":"none";
		document.getElementById(ButtonMainName + i).src=ButtonFolder + "/" + i + (buttonnumber==i?PushedButtonName:RegularButtonName)+ ButtonType;

	}

    timerbutton=setTimeout('ChangeButton()',ChangeButtonTime);
}








function MouseOn(x)

{
	
	PauseSlideShow();
	document.getElementById(PictureLinkID).href="?rightframe=/" + NewsFolder + "/" + x + NewsWebType;
	
	y=x;
	
	for(var i=1;i<=2;i++)
				{
				document.getElementById("Resim" + i).style.filter= x==i?"alpha(opacity:100)":"alpha(opacity:0)";
				document.getElementById(TitleMainName + i).style.display = x==i?"":"none";
				document.getElementById(ButtonMainName + i).src=ButtonFolder + "/" + i + (x==i?PushedButtonName:RegularButtonName)+ ButtonType;
                }

	
}

