if ((/Android|iPhone|iPad|iPod|BlackBerry|Windows Phone/i).test(navigator.userAgent || navigator.vendor || window.opera)) {
	$('body').removeClass('desktop');
}
$(document).ready(function() {


	/* ---	Equal height function resizeable on window resize ---- */
	if ($(window).width() > 880) {
		delay(function() {
			$('.main .col-item').css('height', 'auto'); //solve for all you browser stretchers out there!
			setEqualHeight($('.main .col-item'));
		}, 500);
	}
	$(window).resize(function() {
		if ($(window).width() > 880) {
			delay(function() {
				$('.main .col-item').css('height', 'auto'); //solve for all you browser stretchers out there!
				setEqualHeight($('.main .col-item'));
			}, 500);

		} else {
			$(".main .col-item").css('height', 'auto');
		}
	});



	$(window).on('scroll', function() {
		var delay = 0;
		$('.main .col-item').children().each(function(i) {

			var bottom_of_object = $(this).offset().top + 100;
			var bottom_of_window = $(window).scrollTop() + $(window).height();
			if (bottom_of_window > bottom_of_object) {

				var $li = $(this);
				setTimeout(function() {
					$li.addClass('active');
				}, delay += 100);
			}

		});

	});
	$(window).trigger('scroll');

	if ($(window).width() > 1040) {
		var sliderheight = $('#slider').height();
		var headerheight = $('#header').height();
		var windowheight = $(window).height();
		var imgheight = windowheight - headerheight;
		$('.slider-container img, .slider-container  .bx-wrapper .bx-viewport').css("height", imgheight);
		//$('#left-nav .menu-wrap').css("min-height", imgheight);

	} else {
		$('.slider-container img,.slider-container .bx-wrapper .bx-viewport').css("height", "auto");
		$('.slider-container img, .bx-wrapper .bx-viewport').css("min-height", 90);
		//$('#left-nav .menu-wrap').css("height", "auto");
	}
	$('.slider-container').bxSlider({
		mode: 'fade',
		auto: true,
		autoHover: true,
		adaptiveHeight: false,
		onSliderLoad: function() {
			if ($(window).width() > 1040) {
                        var sliderheight = $('#slider').height();
                        var headerheight = $('#header').height();
                        var windowheight = $(window).height();
                        var leftSliderHeight = $('#slider .slider-info').height();
                        var imgheight = windowheight - headerheight - 10;
                        $('.slider-container img, #slider,.slider-container  .bx-wrapper .bx-viewport').css("max-height", imgheight);
                        $('.slider-container img, #slider, .slider-container  .bx-wrapper .bx-viewport').css("min-height", leftSliderHeight + 60);
                        //$('#left-nav .menu-wrap').css("height", imgheight);
                    } else {
                        $('.slider-container img, #slider,.slider-container  .bx-wrapper .bx-viewport').css("max-height", "100%");
                        $('.slider-container img, #slider, .bx-wrapper .bx-viewport').css("min-height", 90);
                        //$('#left-nav .menu-wrap').css("height", "auto");
                    }
			$(window).resize(function() {
                        if ($(window).width() > 1040) {
                            var sliderheight = $('#slider').height();
                            var headerheight = $('#header').height();
                            var windowheight = $(window).height();
                            var leftSliderHeight = $('#slider .slider-info').height();
                            var imgheight = windowheight - headerheight - 10;
                            $('.slider-container img, #slider, .slider-container  .bx-wrapper .bx-viewport').css("max-height", imgheight);
                            $('.slider-container img, #slider, .slider-container  .bx-wrapper .bx-viewport').css("min-height", leftSliderHeight + 60);
                            // $('#left-nav .menu-wrap').css("height", imgheight);
                        } else {
                            $('.slider-container img, #slider,.slider-container  .bx-wrapper .bx-viewport').css("max-height", "100%");
                            $('.slider-container img, #slider, .slider-container  .bx-wrapper .bx-viewport').css("min-height", 90);
                            //$('#left-nav .menu-wrap').css("height", "auto");
                        }
                    });
		}

	});

});

  $('.news-slider').bxSlider({
      auto: true,
      autoHover: true,
      adaptiveHeight: true,
      slideMargin: 10,
      onSliderLoad: function (currIndex) {
          var newsheight = $(".news:nth-child(" + (currIndex + 2) + ") .right-content").height()+17;
          $(".main .col-item.news-wrapper .bx-wrapper .bx-pager").css({ bottom: newsheight});

				var maxHeight = -1;

			   	$('.news').each(function() {
				   maxHeight = maxHeight > $(this).height() ? maxHeight : $(this).height();
			   	});
				$(".main .col-item.news-wrapper .hover-content").css({height: maxHeight});
			},
      onSlideBefore: function(currentSlideNumber, totalSlideQty, currentSlideHtmlObject) {
          var newsheight = $(".news .right-content").eq(currentSlideHtmlObject + 1).height()+17;
          $(".main .col-item.news-wrapper .bx-wrapper .bx-pager").css({ bottom: newsheight});
      }
  });
