div.script:
    $(function () {
        function _submitSearch() {
            var q = $('#q').val();
            q = q.replace(/\{|\}|\(|\)|\[|\]|\*|\^|\!|\~|\?|\//g, "");

            location.href = "/sys/search?q=" + window.encodeURIComponent(q);
        };

        $("#q").on("keypress", function (event) {
            if ((event.which == 13 || event.keyCode == 13) && this.value.trim() !== "" ){
                _submitSearch();
            }
        });

        $("#q").next().on("click", _submitSearch);
    });

link.script:
    var slideshowID = 'angelo_slideshow_2de6823d37a94036809570a36352415e';
    var duration = 9000;

    /* Demo Scripts for Bootstrap Carousel and Animate.css article
    * on SitePoint by Maria Antonietta Perna
    */
    (function ($) {
        $(document).ready(function () {
            //Function to animate slider captions
            function doAnimations(elems) {
                //Cache the animationend event in a variable
                var animEndEv = 'webkitAnimationEnd animationend';

                elems.each(function () {
                    var $this = $(this),
                        $animationType = $this.data('animation');
                    $this.addClass($animationType).one(animEndEv, function () {
                        $this.removeClass($animationType);
                    });
                });
            }

            //Variables on page load
            var $myCarousel = $('#' + slideshowID),
                $firstAnimatingElems = $myCarousel.find('.item:first').find("[data-animation ^= 'animated']");

            //Initialize carousel
            $myCarousel.carousel({
                interval: duration
            });

            //Animate captions in first slide on page load
            doAnimations($firstAnimatingElems);

            //Pause carousel
            // $myCarousel.carousel('pause');

            //Other slides to be animated on carousel slide event
            $myCarousel.on('slide.bs.carousel', function (e) {
                var $animatingElems = $(e.relatedTarget).find("[data-animation ^= 'animated']");
                doAnimations($animatingElems);
            });
        });
    })(jQuery);

