                        .done(function () {
                    
                            setCookie('AngeloSiteAlertCookie-1d292e55931147a1a589c456c1931a3a-545277ba5da9477a96f200d33ac757f0-20200404-19174475', "sitealert", 365)
                        });
                }

            
