html.script:
(function($) {
	$.fn.hasScrollBar = function() {
		return this.get(0).scrollHeight > this.get(0).clientHeight;
	};

	$("section.sctNews section").each(function() {
		if ($(this).hasScrollBar())
		{
			$(this).css("padding-right", "12px");
			$(this).siblings(".divReadMore").show();
		}
	});
})(jQuery);

function Expand(newsReadMore)
{
	var string_dn = '   expand';
	var string_up = '   collapse';
	var activeNewsItem = $(newsReadMore).siblings("#secReadAll");

	if (activeNewsItem.hasClass("overflow"))
	{
		activeNewsItem.removeClass("overflow");
		$(newsReadMore).html(string_up);
	}
	else
	{
		activeNewsItem.addClass("overflow");
		$(newsReadMore).html(string_dn);
	}
}
