        $(window).resize(function() {
            $('.navbar-collapse').collapse('hide');
            if($( window ).width() <= 767) {

                $('ul.nav').flexMenu({
                    threshold: 999
                });
            }
            else
            {
                $('ul.nav').flexMenu({
                    showOnHover: true,
                    popupClass: 'dropdown-menu'
                });
            }
        });

        $(document).ready(function() {


            $('.navbar-collapse').collapse('hide');
            if($( window ).width() <= 767) {

                $('ul.nav').flexMenu({
                    undo: true
                });
            }
            else
            {
                $('ul.nav').flexMenu({
                    showOnHover: true,
                    popupClass: 'dropdown-menu'
                });
            }

           $(window).scroll(function () {

              if ($(window).scrollTop() > 215) {
                  $('#navbarid').addClass('navbar-fixed');
                  $('#header-whole').addClass('navbar-fixed2');
                  $('#header-color-add').addClass('addheaderbottom');
                  $('#addcol10').addClass('col-md-10');
                  $('.sticky-logo').addClass('logo-fixed');
              }
              if ($(window).scrollTop() < 216) {
                  $('#navbarid').removeClass('navbar-fixed');
                  $('#header-whole').removeClass('navbar-fixed2');
                  $('#header-color-add').removeClass('addheaderbottom');
                  $('#addcol10').removeClass('col-md-10');
                  $('.sticky-logo').removeClass('logo-fixed');
              }
           });

           if ($(window).scrollTop() > 215) {
               $('#navbarid').addClass('navbar-fixed');
               $('#header-whole').addClass('navbar-fixed2');
               $('#header-color-add').addClass('addheaderbottom');
               $('#addcol10').addClass('col-md-10');
               $('.sticky-logo').addClass('logo-fixed');
           }
           if ($(window).scrollTop() < 216) {
               $('#navbarid').removeClass('navbar-fixed');
               $('#header-whole').removeClass('navbar-fixed2');
               $('#header-color-add').removeClass('addheaderbottom');
               $('#addcol10').removeClass('col-md-10');
               $('.sticky-logo').removeClass('logo-fixed');
           }
        });


          $( document ).ready(function() {

               $(document).click(function(event) {
                   if(!$(event.target).closest('.navbar-collapse').length) {
                       if($('.navbar-collapse').hasClass('in')) {
                           $('.navbar-collapse').removeClass('in');
                       }
                   }
               })
          });

        $(document).ready(function() {

          $(window).scroll(function () {
              //if you hard code, then use console
              //.log to determine when you want the
              //nav bar to stick.
            //   console.log($(window).scrollTop())
            if ($(window).scrollTop() > 215) {
              $('#navbarid').addClass('navbar-fixed');
            }
            if ($(window).scrollTop() < 216) {
              $('#navbarid').removeClass('navbar-fixed');
            }
          });
        });

        $(document).ready(function() {
            $('a[href="#contact"]').click(function() {

                changeContact();



                $('#contact_modal').modal('show');
            });

            changeContact();
        });

        function changeContact() {
            var contact_select = $('#contact_select').val();

            $('#contact_select2').val(contact_select);


            $('#contact_map').html('');

            $('#contact_to').val(contact_select);

            $.ajax({
                beforeSend: function(xhr, opts){
                    $('#contact_map').hide();
                    $('#contact_map_loading').show();

                    $('#contact_details_phone').html('');
                    $('#contact_details_fax').html('');
                },
                type: 'GET',
                url: '/includes/get_contact_details.php?id=' + contact_select,
                success: function(data) {

                    var obj = jQuery.parseJSON( data );

                    $.each(obj, function(key, value) {
                        if(key == 'name')
                        {
                            $('#contact_details_name').html(value);
                        }
                        if(key == 'phone')
                        {
                            if(value != '')
                            {
                                $('#contact_details_phone').html('   ' + value);
                            }
                        }
                        if(key == 'fax')
                        {
                            if(value != '')
                            {
                                $('#contact_details_fax').html('   ' + value);
                            }
                        }

                        if(key == 'email')
                        {
                            if(value == '')
                            {
                                $('.contactright').hide();
                                $('.contactleft').removeClass('col-md-8');
                                $('.contactleft').addClass('col-md-12');
                            }
                            else
                            {
                                $('.contactright').show();
                                $('.contactleft').addClass('col-md-8');
                                $('.contactleft').removeClass('col-md-12');
                            }
                        }

                        if(key == 'address')
                        {
                            $('#contact_map').html('');

                            setTimeout(function() {
                                $('#contact_map_loading').hide();

                                $('#contact_map').show();
                            }, 700);
                        }

                    });
                }
            });
        }

        function changeContact2() {


            var contact_select2 = $('#contact_select2').val();

            $('#contact_select').val(contact_select2);
            changeContact();
        }

        function submitContact() {
            var contact_name = $('#contact_name').val();
            var contact_email = $('#contact_email').val();
            var contact_phone = $('#contact_phone').val();
            var contact_message = $('#contact_message').val();
            var contact_to = $('#contact_to').val();

            $.ajax({
                beforeSend: function(xhr, opts){
                    $('#contact_alert').html('');

                    $('#contact_name').attr('disabled', 'disabled');
                    $('#contact_email').attr('disabled', 'disabled');
                    $('#contact_phone').attr('disabled', 'disabled');
                    $('#contact_message').attr('disabled', 'disabled');
                    $('#contact_submit').html(' Please wait...');
                    $('#contact_submit').addClass('disabled');
                },
                data: {
                  contact_name: contact_name,
                  contact_email: contact_email,
                  contact_phone: contact_phone,
                  contact_message: contact_message,
                  contact_to: contact_to
                },
                type: 'POST',
                url: '/includes/submit_contact.php',
                success: function(data) {
                    if(data == '1')
                    {
                        //success
                        $('#contact_alert').html('
 Your message was sent successfully.');

                        $('#contact_name').val('');
                        $('#contact_email').val('');
                        $('#contact_phone').val('');
                        $('#contact_message').val('');
                    }
                    else
                    {
                        $('#contact_alert').html('
 ' + data + '');
                    }

                    $('#contact_name').removeAttr('disabled');
                    $('#contact_email').removeAttr('disabled');
                    $('#contact_phone').removeAttr('disabled');
                    $('#contact_message').removeAttr('disabled');
                    $('#contact_submit').html('Send Message');
                    $('#contact_submit').removeClass('disabled');
                }
            });
        }

