          })

$(window).resize(function () {
  var viewportWidth = $(window).width();

  if (viewportWidth < 768) {
			 $(".rightbodygap").removeClass("margin-r-n20");
			 $(".rightbodygap").removeClass("margin-l-20");
  }

  if (viewportWidth > 768) {
 			$(".rightbodygap").addClass("margin-r-n20");
 			$(".rightbodygap").addClass("margin-l-20");
  }
});

