	function pgScreenSize() {
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
            if(!$(".cs-custom-channel-dropdown-outer").length) {
				megaDropdown();
            }
            $("#hp-slideshow-outer, #sp-content-outer, #spn-content-outer").css("margin-top", $("#gb-fixed-header").height()+30);       
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
            $("#hp-slideshow-outer, #sp-content-outer, #spn-content-outer").css("margin-top", $("#gb-fixed-header").height()+30);      
		}
	}

	function photoGallery(){
		if(!$("#sw-content-container13.region.ui-hp .gallery-container").length){
			// PHOTO GALLERY CONFIGURATION
			$("#sw-content-container13.ui-hp div.ui-widget.app.gallery").creativeGalleryRotate({
				"imageHeight"		: 390,
				"imageControls"		: "on",
				"animateType"		: "fade", // CHOICES - "slide"
				"animateSpeed"		: 3,
				"pauseHover"		: ['all'], // CHOICES - ['title'], ['overlay'], ['caption'], ['all']
				"autoRotate"		: "on",
				"useTitles"		: "on",
				"useCaptions"		: "on",
				"useOverlay"		: "off",
				"randomOrder"		: "off",
				"linkedElement"		: ['picture'], // CHOICES - ['picture'], ['title'], ['overlay']
				"itemizedBullets"	: "on",
				"thumbnails"		: "off",
				"thumbnailWidth"	: 50,
				"thumbnailHeight"	: 32,
				"thumbnailControls"	: "off",
				"thumbnailLimit"	: 3,
				"addClass"		: "unclassed",
				"moreLinkText"		: "off" 
			});
		}
	}

